#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\DB\livescore-app-bot\node_modules\.pnpm\telegraf@4.15.0\node_modules\telegraf\lib\node_modules;C:\DB\livescore-app-bot\node_modules\.pnpm\telegraf@4.15.0\node_modules\telegraf\node_modules;C:\DB\livescore-app-bot\node_modules\.pnpm\telegraf@4.15.0\node_modules;C:\DB\livescore-app-bot\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/DB/livescore-app-bot/node_modules/.pnpm/telegraf@4.15.0/node_modules/telegraf/lib/node_modules:/mnt/c/DB/livescore-app-bot/node_modules/.pnpm/telegraf@4.15.0/node_modules/telegraf/node_modules:/mnt/c/DB/livescore-app-bot/node_modules/.pnpm/telegraf@4.15.0/node_modules:/mnt/c/DB/livescore-app-bot/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../telegraf/lib/cli.mjs" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../telegraf/lib/cli.mjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../telegraf/lib/cli.mjs" $args
  } else {
    & "node$exe"  "$basedir/../telegraf/lib/cli.mjs" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
